angular.module('insight.blocks').controller('BlocksController',
    function ($scope, $rootScope, $routeParams, $location, Global, Block, Blocks, BlockByHeight) {
        $scope.global = Global;
        $scope.loading = false;
        if ($routeParams.blockHeight) {
            BlockByHeight.get({
                blockHeight: $routeParams.blockHeight
            }, function (hash) {
                $location.path('/block/' + hash.blockHash);
            }, function () {
                $rootScope.flashMessage = 'Bad Request';
                $location.path('/');
            });
        }

        //Datepicker
        var _formatTimestamp = function (date) {
            var yyyy = date.getFullYear().toString();
            var mm = (date.getMonth() + 1).toString(); // getMonth() is zero-based
            var dd = date.getDate().toString();
            return yyyy + '-' + (mm[1] ? mm : '0' + mm[0]) + '-' + (dd[1] ? dd : '0' + dd[0]); //padding
        };

        $scope.$watch('dt', function (newValue, oldValue) {
            if (newValue !== oldValue) {
                $location.path('/blocks-date/' + _formatTimestamp(newValue));
            }
        });

        $scope.openCalendar = function ($event) {
            $event.preventDefault();
            $event.stopPropagation();
            $scope.opened = true;
        };

        $scope.humanSince = function (time) {
            var m = moment.unix(time).startOf('day');
            var b = moment().startOf('day');
            return m.max().from(b);
        };

        $scope.list = function () {
            $scope.loading = true;
            var blockDate = $routeParams.blockDate;
            var todayTimestamp = new Date().getTime();
            var paramsTimestamp = new Date(blockDate).getTime();
            if (paramsTimestamp > todayTimestamp) {
                blockDate = _formatTimestamp(new Date());
            }
            var startTimestamp = $routeParams.startTimestamp * 1000;
            if (startTimestamp > paramsTimestamp + 24 * 60 * 60 * 1000) {
                startTimestamp = paramsTimestamp + 16 * 60 * 60 * 1000 - 1;
            }

            if (blockDate) {
                $scope.detail = blockDate;
            }
            if (startTimestamp) {
                var d = new Date(startTimestamp);
                var m = d.getMinutes();
                if (m < 10) m = '0' + m;
                $scope.before = ' before ' + d.getHours() + ':' + m;
            }
            $rootScope.titleDetail = $scope.detail;
            Blocks.get({
                blockDate: blockDate,
                startTimestamp: parseInt(startTimestamp / 1000)
            }, function (res) {
                $scope.loading = false;
                $scope.blocks = res.blocks;
                $scope.pagination = res.pagination;
            });
        };

        $scope.findOne = function () {
            $scope.loading = true;
            Block.get({
                blockHash: $routeParams.blockHash
            }, function (block) {
                $rootScope.titleDetail = block.height;
                $rootScope.flashMessage = null;
                $scope.loading = false;
                $scope.block = block;
            }, function (e) {
                if (e.status === 400) {
                    $rootScope.flashMessage = 'Invalid Transaction ID: ' + $routeParams.txId;
                }
                else if (e.status === 503) {
                    $rootScope.flashMessage = 'Backend Error. ' + e.data;
                } else {
                    $rootScope.flashMessage = 'Block Not Found';
                }
                $location.path('/');
            });
        };
        $scope.params = $routeParams;
    });
