'use strict';

angular.module('insight.amountStatistic').factory('AmountStatistic', function ($resource) {
    return $resource(window.apiPrefix + '/amountStatistic', {
        
    }, {
        get: {
            method: 'GET',
            interceptor: {
                response: function (res) {
                    return res.data;
                },
                responseError: function (res) {
                    if (res.status === 404) {
                        return res;
                    }
                }
            }
        }
    })
})